mapper.select:
        SELECT


--         级联表的字段

        FROM liulangdongwujiuzhu  a



                and a.id in

                #{item}



                and a.liulangdongwujiuzhu_name like CONCAT('%',#{params.liulangdongwujiuzhuName},'%')


                  and a.zan_number >= #{params.zanNumberStart}   


                  and a.zan_number <= #{params.zanNumberEnd}   


                and a.zan_number = #{params.zanNumber}


                  and a.cai_number >= #{params.caiNumberStart}   


                  and a.cai_number <= #{params.caiNumberEnd}   


                and a.cai_number = #{params.caiNumber}


                  and a.liulangdongwujiuzhu_clicknum >= #{params.liulangdongwujiuzhuClicknumStart}   


                  and a.liulangdongwujiuzhu_clicknum <= #{params.liulangdongwujiuzhuClicknumEnd}   


                and a.liulangdongwujiuzhu_clicknum = #{params.liulangdongwujiuzhuClicknum}


                and a.liulangdongwujiuzhu_content like CONCAT('%',#{params.liulangdongwujiuzhuContent},'%')




        order by a.${params.orderBy} desc
